example:
// As a modifier
View()
    .lubaToast(isPresented: $showToast, message: "Saved!", style: .success)

// Linear bar
LubaProgressBar(value: 0.65, showLabel: true)

// Circular ring
LubaCircularProgress(value: 0.65, size: 64)

// Basic rectangle
LubaSkeleton(height: 20)

// Circle
LubaSkeletonCircle(size: 40)

// Text block
LubaSkeletonText(lines: 3)

// Card layout
LubaSkeletonCard()

View()
    .lubaSheet(isPresented: $showSheet, size: .medium) {
        LubaSheetHeader("Settings", onClose: { showSheet = false })
        // content
    }

LubaMenu(items: [
    LubaMenuItem("Edit", icon: Image(systemName: "pencil")) { edit() },
    LubaMenuItem("Delete", icon: Image(systemName: "trash"), role: .destructive) { delete() }
]) {
    LubaIconButton(icon: "ellipsis") {}
}

// As wrapper
LubaTooltip("This field is required") {
    Image(systemName: "info.circle")
}

// As modifier
Text("Label").lubaTooltip("Helpful explanation")
