example:
// As modifier
Text("Header")
    .lubaExpandable(isExpanded: $expanded) {
        Text("Expanded content here")
    }

// As standalone
LubaExpandable(isExpanded: $expanded, showChevron: true) {
    Text("Header")
} content: {
    Text("Details")
}

// As accordion
LubaAccordion(items: items, allowMultiple: false)

ForEach(items) { item in
    ItemRow(item)
        .lubaSwipeable(
            trailing: [
                .delete { deleteItem(item) },
                .archive { archiveItem(item) }
            ]
        )
}

// Shimmer overlay
Text("Loading content")
    .lubaShimmerable(isLoading: isLoading)

// Redacted + shimmer (hides content)
ProfileView()
    .lubaRedactedShimmer(isLoading: isLoading)

// On any view
Image(systemName: "trash")
    .lubaLongPressable(showProgress: true) {
        deleteAllItems()
    }

// Standalone button
LubaLongPressButton(icon: "power", label: "Shut Down") {
    shutdown()
}

VStack {
    Text("Glass Card")
    Text("Frosted content")
}
.padding(LubaSpacing.lg)
.lubaGlass(.regular, tint: LubaColors.accent)

// Glass container for compositing
ZStack {
    GlassCard1()
    GlassCard2()
}
.lubaGlassContainer()

struct BrandStyle: LubaButtonStyling {
    func foregroundColor(isPressed: Bool, colorScheme: ColorScheme) -> Color {
        .white
    }
    func backgroundColor(isPressed: Bool, colorScheme: ColorScheme) -> Color {
        isPressed ? Color.blue.opacity(0.8) : Color.blue
    }
    func borderColor(isPressed: Bool, colorScheme: ColorScheme) -> Color? {
        nil
    }
}

LubaButton("Action", styling: BrandStyle()) { }
